        <div className="mb-3">
          <label htmlFor="quantity" className="form-label">Quantity:</label>
          <input type="number" className="form-control" id="quantity" name="quantity" defaultValue={item.quantity} required />
        </div>
        <div className="mb-3">
          <label htmlFor="category" className="form-label">Category:</label>
          <input type="text" className="form-control" id="category" name="category" defaultValue={item.category} required />
        </div>
        {/* Add other fields as needed */}
        <button type="submit" className="btn btn-primary">Update Item</button>
      </form>
    </div>
  );
};



export default App;
