import React, { useEffect, useState } from 'react';
import { Form, Button } from 'react-bootstrap';
import axios from 'axios';
import { useNavigate, useParams } from 'react-router-dom';

const EditData = () => {
  const [name, setName] = useState('');
  const [description, setDescription] = useState('');
  const [price, setPrice] = useState('');
  const [quantity, setQuantity] = useState('');
  const [category, setCategory] = useState('');
  const [date, setDate] = useState('');
  const { id } = useParams();
  const navigate = useNavigate();

  const getDataFromID = async () => {
    try {
      const response = await axios.get(`https://majestic-chaja-697966.netlify.app/.netlify/functions/api/item/${id}`);
      setName(response.data.name);
      setCategory(response.data.category);
      setDescription(response.data.description);
      setPrice(response.data.price);
      setQuantity(response.data.quantity);
    } catch (error) {
      console.log(error.message);
    }
  };

  useEffect(() => {
    getDataFromID();
  }, []);

  const editData = async (e) => {
    e.preventDefault();
    try {
      await axios.patch(`https://majestic-chaja-697966.netlify.app/.netlify/functions/api/item/${id}`, {
        name,
        description,
        quantity,
        price,
        date,
        category,
      });
      navigate('/');
    } catch (error) {
      console.log(error.message);
    }
  };

  // Internal CSS styles
  const containerStyle = {
    marginTop: '5rem',
  };

  return (
    <div style={containerStyle}>
      <h1 style={{ textAlign: 'center' }}>Edit Item</h1>
      <Form onSubmit={editData}>
        <Form.Group className="mb-3" controlId="name">
          <Form.Label>Name:</Form.Label>
          <Form.Control type="text" value={name} onChange={(e) => setName(e.target.value)} required />
        </Form.Group>
        <Form.Group className="mb-3" controlId="description">
          <Form.Label>Description:</Form.Label>
          <Form.Control as="textarea" rows={3} value={description} onChange={(e) => setDescription(e.target.value)} required />
        </Form.Group>
        <Form.Group className="mb-3" controlId="price">
          <Form.Label>Price:</Form.Label>
          <Form.Control type="number" value={price} onChange={(e) => setPrice(e.target.value)} required />
        </Form.Group>
        <Form.Group className="mb-3" controlId="quantity">
          <Form.Label>Quantity:</Form.Label>
          <Form.Control type="number" value={quantity} onChange={(e) => setQuantity(e.target.value)} required />
        </Form.Group>
        <Form.Group className="mb-3" controlId="category">
          <Form.Label>Category:</Form.Label>
          <Form.Control type="text" value={category} onChange={(e) => setCategory(e.target.value)} required />
        </Form.Group>
        {/* Add other fields as needed */}
        <Button variant="primary" type="submit" style={{ width: '100%' }}>
          Update Item
        </Button>
      </Form>
    </div>
  );
};

export default EditData;
